import React, {Component} from 'react';
import logo from './logo.svg';
import './App.css';

function Display(props){
  return (
    <div className="App">
      <p>{props.value}</p>
    </div>
  );
}
function Button(props){
  return (
   <button onClick={props.onClick}>{props.text} </button>
  );
}

class App extends Component{
  constructor(props){
    super(props);
    this.state = {
      currentOperand: ''
    };
    this.addNumber = this.addNumber.bind(this);
  }

  addNumber(){
    this.setState(
      {
        currentOperand: this.state.currentOperand + "0"
      }
    );
  }
  render(){
    return (
      <div className="App">
        <Display value={this.state.currentOperand}/>
        <Button text="0" click={this.addNumber}/>
        <Button text="1" click={this.addNumber}/>
        <Button text="2" click={this.addNumber}/>
        <Button text="3" click={this.addNumber}/>
        <Button text="4" click={this.addNumber}/>
        <Button text="5" click={this.addNumber}/>
        <Button text="6" click={this.addNumber}/>
        <Button text="7" click={this.addNumber}/>
        <Button text="8" click={this.addNumber}/>
        <Button text="9" click={this.addNumber}/>
        <Button text="+"/>
        <Button text="-"/>
        <Button text="*"/>
        <Button text="/"/>
      </div>
    );
  }
}

export default App;
