import React, {Component} from 'react';
import logo from './logo.svg';
import './App.css';

function Display(props){
  return (
    <div className="App">
      <p>{props.value}</p>
    </div>
  );
}
function Button(props){
  return (
   <button onClick={props.click}>{props.text} </button>
  );
}

class App extends Component{
  constructor(props){
    super(props);
    this.state = {
      currentOperand: '',
      oldOperand: '',
      operation: '',
      pilha: [],
      bolVirgula: false
    };
    this.addNumber = this.addNumber.bind(this);
    this.apagar = this.apagar.bind(this);
    this.op = this.op.bind(this);
    this.igual = this.igual.bind(this);
    this.apagarTudo = this.apagarTudo.bind(this);
    this.regMem = this.regMem.bind(this);
  }

  addNumber(number){
    const { bolVirgula,currentOperand } = this.state
    if (number !== ".") {
      this.setState({
        currentOperand: this.state.currentOperand + number
      })
    } else if (number === "." & bolVirgula===false) {
      this.setState({
        currentOperand: this.state.currentOperand + number,
        bolVirgula: true
      })
    } else if (number === "." & bolVirgula===true) {
      this.setState({
        currentOperand: currentOperand,
      })
    }
  }
  apagar(){
    this.setState(
      {
        currentOperand: "",
        bolVirgula: false
      }
    );
  }

  op(atual,op){
    this.setState(
      {
        oldOperand: atual + op,
        currentOperand: "",
        operation: op,
        bolVirgula: false
      }
    );
  }
  igual(atual,ant,op){
    this.setState((state) => {
        let comp;
        switch(op) {
          case '+':
            comp = parseFloat(atual) + parseFloat(ant);
            break;
          case '-':
            comp = parseFloat(ant) - parseFloat(atual) ;
            break;
          case '/':
            comp = parseFloat(ant) / parseFloat(atual);
            break;
          case '*':
            comp = parseFloat(atual) * parseFloat(ant)
            break;
          default:
            return;
        }
      return {
        currentOperand: comp,
        oldOperand: '',
        operation: '',
      }
      }
    );
  }
  apagarTudo(){
    this.setState(
      {
        oldOperand: "",
        currentOperand: "",
        operation: "",
        bolVirgula: false
      }
    );
  }
  regMem(){
    this.setState((state) => {
      const {oldOperand, pilha} = state;
      const prev = pilha;
      const current = parseFloat(oldOperand);
      return{
        pilha: prev.push(current)
      }
    }
    );
  }





  render(){
    return (
      <div className="App">
        <article>
          <header> 
            <h1> Calculadora</h1>
          </header>
          <main class="Corpo">
            <section class = "visor">
              <h1><Display value={this.state.oldOperand}/></h1>
              <h3><Display value={this.state.currentOperand}/></h3>
            </section>
            <section class="botoes">
              <Button text="0" click={() => this.addNumber("0")}/>
              <Button text="1" click={() => this.addNumber("1")}/>
              <Button text="2" click={() => this.addNumber("2")}/>
              <Button text="3" click={() => this.addNumber("3")}/>
              <Button text="4" click={() => this.addNumber("4")}/>
              <Button text="5" click={() => this.addNumber("5")}/>
              <Button text="6" click={() => this.addNumber("6")}/>
              <Button text="7" click={() => this.addNumber("7")}/>
              <Button text="8" click={() => this.addNumber("8")}/>
              <Button text="9" click={() => this.addNumber("9")}/>
              <Button text="." click={() => this.addNumber(".")}/>
              <Button text="AC" click={() => this.apagar()}/>
              <Button text="+" click={() => this.op(this.state.currentOperand,'+')}/>
              <Button text="-" click={() => this.op(this.state.currentOperand,'-')}/>
              <Button text="*" click={() => this.op(this.state.currentOperand,'*')}/>
              <Button text="/" click={() => this.op(this.state.currentOperand,'/')}/>
              <Button text="=" click={() => this.igual(this.state.currentOperand,this.state.oldOperand,this.state.operation)}/>
              <Button text="Clear All" click={() => this.apagarTudo()}/>   
              <Button text="M+" click={() => this.regMem(this.state.oldOperand,this.state.pilha)}/>   
            </section>
            <section class="memoria">
              <h1> Memoria </h1>
              {
                this.state.pilha.map(
                  (el) => <p>{el}</p>
                )
              }
            </section>
          </main>
        </article>
      </div>
    );
  }
}

export default App;
