import React, {Component} from 'react';
import logo from './logo.svg';
import './App.css';

function Display(props){
  return (
    <div className="App">
      <p>{props.value}</p>
    </div>
  );
}
function Button(props){
  return (
   <button onClick={props.click}>{props.text} </button>
  );
}

class App extends Component{
  constructor(props){
    super(props);
    this.state = {
      currentOperand: '',
      oldOperand: '',
      operation: ''
    };
    this.addNumber = this.addNumber.bind(this);
    this.apagar = this.apagar.bind(this);
  }

  addNumber(number){
    this.setState(
      {
        currentOperand: this.state.currentOperand + number
      }
    );
  }
  apagar(){
    this.setState(
      {
        currentOperand: ""
      }
    );
  }

  op(atual,op){
    this.setState(
      {
        oldOperand: atual + op,
        currentOperand: "",
        operation: op
      }
    );
  }
  igual(atual,ant,op){
    this.setState((state) => {
        let comp;
        switch(op) {
          case '+':
            comp = parseFloat(atual) + parseFloat(ant);
            break;
          case '-':
            comp = parseFloat(ant) - parseFloat(atual) ;
            break;
          case '/':
            comp = parseFloat(ant) / parseFloat(atual);
            break;
          case '*':
            comp = parseFloat(atual) * parseFloat(ant)
            break;
          default:
            return;
        }
      return {
        currentOperand: comp,
        oldOperand: '',
        operation: '',
      }
      }
    );
  }


  render(){
    return (
      <div className="App">
        <article>
          <header> 
            <h1> Calculadora</h1>
          </header>
          <main class="Corpo">
            <section class = "visor">
              <h1><Display value={this.state.oldOperand}/></h1>
              <h3><Display value={this.state.currentOperand}/></h3>
            </section>
            <section class="botoes">
              <Button text="0" click={() => this.addNumber("0")}/>
              <Button text="1" click={() => this.addNumber("1")}/>
              <Button text="2" click={() => this.addNumber("2")}/>
              <Button text="3" click={() => this.addNumber("3")}/>
              <Button text="4" click={() => this.addNumber("4")}/>
              <Button text="5" click={() => this.addNumber("5")}/>
              <Button text="6" click={() => this.addNumber("6")}/>
              <Button text="7" click={() => this.addNumber("7")}/>
              <Button text="8" click={() => this.addNumber("8")}/>
              <Button text="9" click={() => this.addNumber("9")}/>
              <Button text="AC" click={() => this.apagar()}/>
              <Button text="+" click={() => this.op(this.state.currentOperand,'+')}/>
              <Button text="-" click={() => this.op(this.state.currentOperand,'-')}/>
              <Button text="*" click={() => this.op(this.state.currentOperand,'*')}/>
              <Button text="/" click={() => this.op(this.state.currentOperand,'/')}/>
              <Button text="=" click={() => this.igual(this.state.currentOperand,this.state.oldOperand,this.state.operation)}/>
            </section>
          </main>
        </article>
      </div>
    );
  }
}

export default App;
